import React, { useState } from "react";
import "./Documents.css";

function Documents() {
  const [docs, setDocs] = useState([]);

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setDocs((prev) => [
      ...prev,
      {
        name: file.name,
        type: file.type,
        version: prev.filter((d) => d.name === file.name).length + 1,
        uploadedAt: new Date(),
      },
    ]);
  };

  return (
    <div className="documents">
      <h2>Documents</h2>
      <input type="file" onChange={handleUpload} />
      <div className="document-list">
        {docs.map((doc, i) => (
          <div key={i} className="doc-card">
            <h4>{doc.name}</h4>
            <p>Type: {doc.type}</p>
            <p>Version: v{doc.version}</p>
            <p>Uploaded: {doc.uploadedAt.toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Documents;
