import React from "react";

function Home() {
  const name = localStorage.getItem("name");
  const role = localStorage.getItem("role");

  return (
    <div>
      <h2>Home</h2>
      {name ? (
        <p>Welcome, {name}! You are logged in as <strong>{role}</strong>.</p>
      ) : (
        <p>Please log in first.</p>
      )}

      {role === "partner" && (
        <div>
          <h3>Partner Dashboard</h3>
          <p>Manage cases, assign tasks, and control access.</p>
        </div>
      )}

      {role === "client" && (
        <div>
          <h3>Client Dashboard</h3>
          <p>View your case files, upload documents, and chat with your lawyer.</p>
        </div>
      )}
    </div>
  );
}

export default Home;
